const std = @import("std");
const testing = std.testing;

// WTF? Why does packed not work?
// maybe related to: https://github.com/ziglang/zig/issues/9544
const TarHeader = extern struct {
    file_name: [100]u8,
    file_mode: [8]u8,
    uid: [8]u8,
    gid: [8]u8,
    file_size: [12]u8,
    last_modified: [12]u8,
    header_checksum: [8]u8,
    link_indicator: u8,
    linked_name: [100]u8,
    padding: [255]u8,

    const Self = @This();

    fn validate(self: *Self) bool {
        for (self.file_size[0..11]) |c| {
            if ('0' > c or '7' < c) {
                return false;
            }
        }

        if ('\x00' != self.file_size[11]) {
            return false;
        }

        return true;
    }

    fn fileName(self: *Self) []u8 {
        for (self.file_name) |c, i| {
            if (0 == c) {
                return self.file_name[0..i];
            }
        }

        return self.file_name[0..self.file_name.len];
    }

    fn fileSize(self: *Self) u64 {
        return std.fmt.parseInt(u64, self.file_size[0..11], 8) catch unreachable;
    }
};

const tar_block_size = 512;

comptime {
    std.debug.assert(@sizeOf(TarHeader) == 512);
}

const TarFileType = enum(u8) {
    normal = '0',
    hard_link = '1',
    symbolic_link = '2',
    character_special = '3',
    block_special = '4',
    directory = '5',
    fifo = '6',
    contiguous_file = '7',
};

const TarFile = struct {
    file_name: [:0]u8,
    file_type: TarFileType,
    file_size: u64,
};

fn TarIterator(ReaderType: anytype, SeekableStreamType: anytype) type {
    return struct {
        allocator: *std.mem.Allocator,
        reader: Self.Reader,
        seekable_stream: Self.SeekableStream,
        next_file_offset: i64,

        const Self = @This();
        const Reader = ReaderType;
        const SeekableStream = SeekableStreamType;
        const Error = error{ UnexpectedEOF, FormatError } ||
            Self.Reader.Error ||
            Self.SeekableStream.SeekError ||
            std.mem.Allocator.Error;

        fn init(
            allocator: *std.mem.Allocator,
            reader: Self.Reader,
            seekable_stream: Self.SeekableStream,
        ) Self {
            return Self{
                .reader = reader,
                .seekable_stream = seekable_stream,
                .allocator = allocator,
                .next_file_offset = 0,
            };
        }

        // NOTE: One should free TarFile.file_name to avoid leaks.
        fn next(self: *Self) Self.Error!?TarFile {
            if (0 != self.next_file_offset) {
                try self.seekable_stream.seekBy(self.next_file_offset);
            }

            var buf: [@sizeOf(TarHeader)]u8 = undefined;
            if (@sizeOf(TarHeader) > try self.reader.readAll(buf[0..buf.len])) {
                return error.UnexpectedEOF;
            }

            if (std.mem.allEqual(u8, buf[0..buf.len], 0)) {
                // We expect at least two zero entries at the end of the file.

                if (@sizeOf(TarHeader) > try self.reader.readAll(buf[0..buf.len])) {
                    return error.UnexpectedEOF;
                }

                if (!std.mem.allEqual(u8, buf[0..buf.len], 0)) {
                    return error.FormatError;
                }

                return null;
            }

            const tar_header = @ptrCast(*TarHeader, &buf);

            if (!tar_header.validate()) {
                return error.FormatError;
            }

            const file_size = tar_header.fileSize();
            const block_leftover = file_size % tar_block_size;
            const file_block_size = if (0 == block_leftover) file_size else file_size + tar_block_size - block_leftover;

            self.next_file_offset = @intCast(i64, file_block_size);

            return TarFile{
                .file_name = try self.allocator.dupeZ(u8, tar_header.fileName()),
                .file_type = TarFileType.normal,
                .file_size = file_size,
            };
        }
    };
}

