test "basic tar test" {
    const TarStream = std.io.FixedBufferStream(@TypeOf(test_tar));
    var stream = TarStream{
        .buffer = test_tar,
        .pos = 0,
    };

    const FileTarIterator = TarIterator(TarStream.Reader, TarStream);

    var iter = FileTarIterator.init(testing.allocator, stream.reader(), stream);

    {
        const test_dir_opt = try iter.next();
        try std.testing.expect(null != test_dir_opt);

        const test_dir = test_dir_opt.?;
        defer testing.allocator.free(test_dir.file_name);

        try std.testing.expect(std.mem.eql(u8, test_dir.file_name, "test/"));
        try std.testing.expect(0 == test_dir.file_size);
    }

    {
        const test_hello_file_opt = try iter.next();
        const test_hello_file = test_hello_file_opt.?;
        defer testing.allocator.free(test_hello_file.file_name);

        try std.testing.expect(std.mem.eql(u8, test_hello_file.file_name, "test/hello.txt"));
        try std.testing.expect(0 == test_hello_file.file_size);
    }

    try std.testing.expect(null == try iter.next());
}
